import React from 'react';
import { AiOutlineSend } from 'react-icons/ai';

const Community = () => {
  const channelsList = ["1", "2", "3", "4", "5", "6", "7", "8"];
  const chatList = [{ "time": "2:00 AM", "sender": "sharan", "message": "Hello" }];

  return (
    <div className='flex flex-col'>
      <div className='flex'>
        <section className='bg-[#212121] min-w-[250px] max-w-[250px] flex flex-col border border-black overflow-y-auto'>
          <div className='flex justify-center items-center text-white h-20'>Channels</div>
          <Channels channelsList={channelsList} />
        </section>
        <section className='overflow-y-scroll flex-1'>
          <Chats chats={chatList} />
        </section>
      </div>
      <div className='flex justify-between bottom-0 sticky'>
        <input placeholder='Enter message' type="text" className='flex justify-end items-baseline border border-black w-full h-14 focus:outline-none p-5 text-xl'></input>
        <button className='border border-black w-20 text-white text-3xl flex justify-center items-center'><AiOutlineSend /></button>
      </div>
    </div>
  );
};

export default Community;

const Channels = ({ channelsList }) => {
  return (
    <>
      {channelsList.map((channel) => (
        <a href="#" key={channel}><Channel channelData={channel} /></a>
      ))}
    </>
  );
};

const Channel = ({ channelData }) => {
  return (
    <div className='text-white border border-black border-collapse h-12 flex justify-center items-center'>
      {channelData}
    </div>
  );
};

const Chats = ({ chats }) => {
  return (
    <>
      {chats.map((chat) => (
        <Chat chat={chat} key={chat.time} />
      ))}
    </>
  );
};

const Chat = ({ chat }) => {
  return (
    <div>
      <div className="mx-10 my-5 h-20 flex flex-col bg-black text-white p-5">
        {chat.message}
      </div>
    </div>
  );
};
